sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var requestUser = request.pathParams.user_name;
	var requestNIQuery = request.queryParams.ni_query;

	var userGR = new GlideRecord('sys_user');
	userGR.get('user_name',requestUser);

	var activeNeedItGA = new GlideAggregate('x_58872_needit_needit');
	activeNeedItGA.addAggregate('COUNT');
	activeNeedItGA.addQuery('u_requested_for', userGR.sys_id);
	activeNeedItGA.addEncodedQuery(requestNIQuery);
	activeNeedItGA.groupBy('u_request_type');
	activeNeedItGA.query();

	var needitRecs = 0;
	var niUserRecs = [];
	var needitRecsCount = 0;

	while (activeNeedItGA.next()) {

		needitRecs = activeNeedItGA.getAggregate('COUNT');
		var reqType = activeNeedItGA.u_request_type.getDisplayValue();
		niUserRecs.push({"requestType": reqType, "count": needitRecs});
		needitRecsCount = parseInt(needitRecsCount) + parseInt(needitRecs);

	}

	var body = {};
	body.totalUserNIRecs = needitRecsCount;
	body.catCounts = niUserRecs;
	body.user = {"User name": userGR.user_name,
"User ID": userGR.sys_id};

	response.setBody(body);


})(request, response);
